@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
    min-height: 100vh;
  }

}

@layer components {
  .card-hover {
    @apply transition-all duration-300 hover:shadow-lg;
  }

  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: 1px solid var(--glass-border);
    box-shadow:
      0 8px 32px 0 var(--glass-shadow),
      0 1px 0 0 var(--glass-inset) inset,
      0 0 0 1px rgba(255, 255, 255, 0.1) inset;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-card:hover {
    border-color: rgba(255, 255, 255, 0.2);
    box-shadow:
      0 12px 40px 0 var(--glass-shadow),
      0 1px 0 0 var(--glass-inset) inset,
      0 0 0 1px rgba(255, 255, 255, 0.15) inset;
  }

  /* Light theme specific borders for Kanban cards */
  :root .glass-card {
    border: 1px solid rgba(0, 0, 0, 0.1);
  }

  :root .glass-card:hover {
    border-color: rgba(0, 0, 0, 0.15);
  }

  /* Kanban column headers in light theme */
  :root .kanban-header {
    border: 1px solid rgba(0, 0, 0, 0.08);
  }

  /* Card titles and content borders in light theme */
  :root .kanban-card {
    border: 1px solid rgba(0, 0, 0, 0.12);
  }

  :root .kanban-card:hover {
    border-color: rgba(0, 0, 0, 0.18);
  }

  .glass-button {
    background: var(--glass-bg);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
    border: 1px solid var(--glass-border);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }


  .glass-button:active {
    background: var(--active-glass);
  }

  .tag {
    @apply inline-flex items-center rounded-full px-3 py-1 text-xs font-medium glass-card;
  }

}


@keyframes slide-up {
  from {
    opacity: 0;
    transform: translateY(30px);
  }

  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fade-in-scale {
  from {
    opacity: 0;
    transform: scale(0.9);
  }

  to {
    opacity: 1;
    transform: scale(1);
  }
}

.slide-up {
  animation: slide-up 0.3s ease-out forwards;
}

.fade-in-scale {
