import React, { useState, useEffect } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Plus, 
  Search, 
  Filter, 
  MoreHorizontal, 
  Trash2,
  PenLine,
  Phone,
  Mail,
  ClipboardList
} from "lucide-react";
import { 
  Card, 
  CardContent,
  CardHeader,
  CardTitle
} from "@/components/ui/card";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from '@/hooks/use-toast';
import { buscarClientes, criarCliente, ApiError, type Cliente } from '@/services/api';

export function Clients() {
  const { toast } = useToast();
  const [clients, setClients] = useState<Cliente[]>([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [clientDialogOpen, setClientDialogOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [isCreating, setIsCreating] = useState(false);
  const [newClientData, setNewClientData] = useState({
    nome: '',
    email: '',
    telefone: '',
    nomeEmpresa: '', // Atualizado para o novo nome do campo
    cargo: '',
    endereco: '',
    observacoes: '',
    status: 'ATIVO',
    fonte: '',
    tags: [] as string[]
  });

  // Carregar clientes na inicializa√ß√£o
  useEffect(() => {
    carregarClientes();
  }, []);

  const carregarClientes = async () => {
    try {
      setIsLoading(true);
      const response = await buscarClientes();
      setClients(response.clientes || response || []);
    } catch (error) {
      console.error('Erro ao carregar clientes:', error);
      toast({
        title: "Erro ao carregar clientes",
        description: "N√£o foi poss√≠vel carregar a lista de clientes",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const filteredClients = clients.filter(client => 
    client.nome.toLowerCase().includes(searchQuery.toLowerCase()) || 
    (client.nomeEmpresa && client.nomeEmpresa.toLowerCase().includes(searchQuery.toLowerCase())) ||
    client.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleCreateClient = async () => {
    if (!newClientData.nome.trim()) {
      toast({
        title: "Erro de valida√ß√£o",
        description: "O nome √© obrigat√≥rio",
        variant: "destructive"
      });
      return;
    }

    try {
      setIsCreating(true);
      const response = await criarCliente(newClientData);

      // Adicionar o novo cliente √† lista
      setClients(prev => [response.cliente || response, ...prev]);

      toast({
        title: "Cliente criado com sucesso!",
        description: `${newClientData.nome} foi adicionado aos seus contatos.`,
      });

      // Limpar formul√°rio e fechar dialog
      setNewClientData({
        nome: '',
        email: '',
        telefone: '',
        nomeEmpresa: '', // Atualizado
        cargo: '',
        endereco: '',
        observacoes: '',
        status: 'ATIVO',
        fonte: '',
        tags: []
      });
      setClientDialogOpen(false);
    } catch (error) {
      console.error('Erro ao criar cliente:', error);

      let errorMessage = 'Erro interno do servidor';
      if (error instanceof ApiError) {
        errorMessage = error.message;
      }

      toast({
        title: "Erro ao criar cliente",
        description: errorMessage,
        variant: "destructive"
      });
    } finally {
      setIsCreating(false);
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">Clientes</h1>
            <p className="text-muted-foreground">Carregando clientes...</p>
          </div>
        </div>
        <Card>
          <CardContent className="p-8 text-center">
            <div className="space-y-2">
              <h3 className="font-medium">Carregando...</h3>
              <p className="text-sm text-muted-foreground">
                Buscando seus clientes no servidor.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">Clientes</h1>
          <p className="text-muted-foreground">
            Gerencie seus contatos e acompanhe intera√ß√µes.
          </p>
        </div>
        <Button onClick={() => setClientDialogOpen(true)}>
          <Plus className="mr-2 h-4 w-4" />
          Novo Cliente
        </Button>
      </div>

      <Card>
        <CardHeader className="p-4">
          <div className="flex flex-col md:flex-row gap-4 justify-between">
            <div className="relative flex-1">
              <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-muted-foreground" />
              <Input
                type="search"
                placeholder="Buscar clientes..."
                className="pl-9 w-full md:w-[300px]"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            <div className="flex gap-2">
              <Button variant="outline" size="sm">
                <Filter className="mr-2 h-4 w-4" />
                Filtros
              </Button>
              <Button variant="outline" size="sm">
                Exportar
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Nome / Empresa</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Telefone</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Data de Registro</TableHead>
                  <TableHead>Tags</TableHead>
                  <TableHead className="text-right">A√ß√µes</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredClients.map((client) => (
                  <TableRow key={client.id}>
                    <TableCell>
                      <div>
                        <div className="font-medium">{client.nome}</div>
                        <div className="text-xs text-muted-foreground">
                          {client.nomeEmpresa}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>{client.email}</TableCell>
                    <TableCell>{client.telefone}</TableCell>
                    <TableCell>
                      <Badge variant={client.status === 'ATIVO' ? 'default' : 'secondary'}>
                        {client.status}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      {client.criadoEm ? format(new Date(client.criadoEm), "dd/MM/yyyy", { locale: ptBR }) : 'N/A'}
                    </TableCell>
                    <TableCell>
                      <div className="flex flex-wrap gap-1">
                        {client.tags?.map((tag) => (
                          <Badge key={tag} variant="outline" className="text-xs">
                            {tag}
                          </Badge>
                        ))}
                      </div>
                    </TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button variant="ghost" size="icon">
                          <Phone className="h-4 w-4" />
                        </Button>
                        <Button variant="ghost" size="icon">
                          <Mail className="h-4 w-4" />
                        </Button>
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="icon">
                              <MoreHorizontal className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuLabel>Op√ß√µes</DropdownMenuLabel>
                            <DropdownMenuSeparator />
                            <DropdownMenuItem>
                              <PenLine className="mr-2 h-4 w-4" /> Editar
                            </DropdownMenuItem>
                            <DropdownMenuItem>
                              <ClipboardList className="mr-2 h-4 w-4" /> Ver Hist√≥rico
                            </DropdownMenuItem>
                            <DropdownMenuItem className="text-destructive">
                              <Trash2 className="mr-2 h-4 w-4" /> Excluir
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>

          {filteredClients.length === 0 && (
            <div className="p-8 text-center">
              <div className="space-y-2">
                <h3 className="font-medium">Nenhum cliente encontrado</h3>
                <p className="text-sm text-muted-foreground">
                  {clients.length === 0 
                    ? "Crie seu primeiro cliente clicando no bot√£o 'Novo Cliente'."
                    : "Nenhum cliente corresponde aos crit√©rios de busca."
                  }
                </p>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* New Client Dialog */}
      <Dialog open={clientDialogOpen} onOpenChange={setClientDialogOpen}>
        <DialogContent className="sm:max-w-[550px]">
          <DialogHeader>
            <DialogTitle>Novo Cliente</DialogTitle>
            <DialogDescription>
              Preencha os dados do novo cliente. Clique em salvar quando terminar.
            </DialogDescription>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="nome">Nome *</Label>
                <Input 
                  id="nome" 
                  placeholder="Nome completo" 
                  value={newClientData.nome}
                  onChange={(e) => setNewClientData(prev => ({ ...prev, nome: e.target.value }))}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="nomeEmpresa">Empresa</Label>
                <Input 
                  id="nomeEmpresa" 
                  placeholder="Nome da empresa" 
                  value={newClientData.nomeEmpresa}
                  onChange={(e) => setNewClientData(prev => ({ ...prev, nomeEmpresa: e.target.value }))}
                />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="email">Email</Label>
                <Input 
                  id="email" 
                  type="email" 
                  placeholder="email@exemplo.com" 
                  value={newClientData.email}
                  onChange={(e) => setNewClientData(prev => ({ ...prev, email: e.target.value }))}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="telefone">Telefone</Label>
                <Input 
                  id="telefone" 
                  placeholder="(99) 99999-9999" 
                  value={newClientData.telefone}
                  onChange={(e) => setNewClientData(prev => ({ ...prev, telefone: e.target.value }))}
                />
              </div>
            </div>
            <div className="space-y-2">
              <Label htmlFor="cargo">Cargo</Label>
              <Input 
                id="cargo" 
                placeholder="Cargo na empresa" 
                value={newClientData.cargo}
                onChange={(e) => setNewClientData(prev => ({ ...prev, cargo: e.target.value }))}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="observacoes">Observa√ß√µes</Label>
              <Textarea 
                id="observacoes"
                placeholder="Informa√ß√µes adicionais sobre o cliente..."
                rows={3}
                value={newClientData.observacoes}
                onChange={(e) => setNewClientData(prev => ({ ...prev, observacoes: e.target.value }))}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setClientDialogOpen(false)}>
              Cancelar
            </Button>
            <Button onClick={handleCreateClient} disabled={isCreating}>
              {isCreating ? "Salvando..." : "Salvar Cliente"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

src/pages/Login.tsx.content:
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Eye, EyeOff, ArrowLeft } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { login, ApiError } from '@/services/api';

export const Login: React.FC = () => {
  const navigate = useNavigate();
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    email: '',
    senha: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    // Login demo - credenciais aceitas
    const isDemo = (formData.email === 'demo@crm.com' && formData.senha === 'demo123') ||
                   (formData.email === 'admin@crm.com' && formData.senha === 'admin123');

    try {
      if (isDemo) {
        // Simular delay de login
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Salvar dados demo no localStorage
        const demoUser = {
          id: 1,
          nome: formData.email === 'admin@crm.com' ? 'Admin Demo' : 'Usuario Demo',
          email: formData.email,
          empresaId: 1,
          token: 'demo-token-' + Date.now()
        };

        localStorage.setItem('crm-user', JSON.stringify(demoUser));
        localStorage.setItem('crm-token', demoUser.token);

        toast({
          title: "Login demo realizado com sucesso!",
          description: `Bem-vindo, ${demoUser.nome}!`,
        });

        navigate('/dashboard');
      } else {
        // Tentar login real
        const response = await login(formData);

        toast({
          title: "Login realizado com sucesso!",
          description: `Bem-vindo, ${response.usuario?.nome || 'usu√°rio'}!`,
        });

        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Erro no login:', error);

      let errorMessage = 'Erro interno do servidor';

      if (error instanceof ApiError) {
        if (error.status === 401) {
          errorMessage = 'Email ou senha incorretos';
        } else if (error.status === 403) {
          errorMessage = 'Conta inativa ou assinatura vencida';
        } else {
          errorMessage = error.message;
        }
      }

      toast({
        title: "Erro no login",
        description: errorMessage,
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const fillDemoCredentials = (email: string, password: string) => {
    setFormData({
      email: email,
      senha: password
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary/10 to-background flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1">
          <div className="flex items-center gap-2 mb-4">
            <Button variant="ghost" size="sm" asChild>
              <Link to="/">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Voltar
              </Link>
            </Button>
          </div>
          <CardTitle className="text-2xl font-bold text-center">
            Entrar no VisionCRM
          </CardTitle>
          <p className="text-center text-muted-foreground">
            Acesse sua conta para continuar
          </p>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                name="email"
                type="email"
                placeholder="seu@email.com"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="senha">Senha</Label>
              <div className="relative">
                <Input
                  id="senha"
                  name="senha"
                  type={showPassword ? "text" : "password"}
                  placeholder="Digite sua senha"
                  value={formData.senha}
                  onChange={handleChange}
                  required
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
            </div>

            <div className="flex items-center justify-between text-sm">
              <Link 
                to="/register" 
                className="text-primary hover:underline"
              >
                N√£o tem conta? Registre-se
              </Link>
              <Link 
                to="/forgot-password" 
                className="text-muted-foreground hover:underline"
              >
                Esqueceu a senha?
              </Link>
            </div>

            <Button 
              type="submit" 
              className="w-full" 
              disabled={isLoading}
            >
              {isLoading ? "Entrando..." : "Entrar"}
            </Button>
          </form>

          <div className="mt-6 text-center space-y-3">
            <div className="text-sm text-muted-foreground">
              <strong>Acesso Demo - Clique para preencher:</strong>
            </div>
            <div className="space-y-2">
              <Button
                type="button"
                variant="outline"
                size="sm"
                className="w-full text-xs"
                onClick={() => fillDemoCredentials('demo@crm.com', 'demo123')}
                data-testid="button-demo-user"
              >
                üìß demo@crm.com | üîë demo123
              </Button>
              <Button
                type="button"
                variant="outline"
                size="sm"
                className="w-full text-xs"
                onClick={() => fillDemoCredentials('admin@crm.com', 'admin123')}
                data-testid="button-admin-user"
              >
                üìß admin@crm.com | üîë admin123
              </Button>
            </div>
            <Link 
              to="/plans" 
              className="inline-block text-sm text-muted-foreground hover:underline mt-4"
            >
              Ver planos dispon√≠veis
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

