/**
 * Servi√ßo de API para intera√ß√£o com o backend
 * Detecta automaticamente o ambiente (localhost, Replit, VPS)
 */

import { detectBrowserEnvironment, logEnvironmentConfig } from '@shared/environment';

// Configura√ß√£o autom√°tica do ambiente
const environmentConfig = detectBrowserEnvironment();
const API_BASE_URL = environmentConfig.apiBaseUrl!;

// Log da configura√ß√£o para debug
if (import.meta.env.DEV) {
  logEnvironmentConfig(environmentConfig, 'frontend');
}

/**
 * Classe para lidar com erros da API
 */
class ApiError extends Error {
  status: number;

  constructor(message: string, status: number) {
    super(message);
    this.status = status;
    this.name = 'ApiError';
  }
}

/**
 * Fun√ß√£o helper para fazer requisi√ß√µes HTTP
 */
async function apiRequest(endpoint: string, options: RequestInit = {}) {
  const url = `${API_BASE_URL}${endpoint}`;

  const config: RequestInit = {
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
    ...options,
  };

  // Adicionar token de autentica√ß√£o se dispon√≠vel
  const token = localStorage.getItem('authToken');
  if (token) {
    config.headers = {
      ...config.headers,
      'Authorization': `Bearer ${token}`,
    };
  }

  try {
    const response = await fetch(url, config);

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}));
      throw new ApiError(
        errorData.erro || `HTTP ${response.status}: ${response.statusText}`,
        response.status
      );
    }

    const contentType = response.headers.get('content-type');
    if (contentType && contentType.includes('application/json')) {
      return await response.json();
    }

    return response;
  } catch (error) {
    if (error instanceof ApiError) {
      throw error;
    }
    throw new ApiError(`Erro de conex√£o: ${error}`, 0);
  }
}

// ==================== AUTENTICA√á√ÉO ====================

export interface LoginCredentials {
  email: string;
  senha: string;
}

export interface RegisterData {
  nome: string;
  email: string;
  senha: string;
  empresaNome?: string;
}

export async function login(credentials: LoginCredentials) {
  const response = await apiRequest('/auth/login', {
    method: 'POST',
    body: JSON.stringify(credentials),
  });

  if (response.token) {
    localStorage.setItem('authToken', response.token);
  }

  return response;
}

export async function register(userData: RegisterData) {
  const response = await apiRequest('/auth/register', {
    method: 'POST',
    body: JSON.stringify(userData),
  });

  if (response.token) {
    localStorage.setItem('authToken', response.token);
  }

  return response;
}

export async function logout() {
  try {
    await apiRequest('/auth/logout', { method: 'POST' });
  } catch (error) {
    console.error('Erro ao fazer logout:', error);
  } finally {
    localStorage.removeItem('authToken');
  }
}

