      dueDate: new Date(Date.now() + 172800000),
      assignedTo: 'Voc√™',
      completed: false
    }
  ];

  const mockClients = [
    { id: '1', name: 'Jo√£o Silva' },
    { id: '2', name: 'Maria Santos' }
  ];

  // Get client name by ID
  const getClientName = (clientId?: string) => {
    if (!clientId) return '';
    const client = mockClients.find(c => c.id === clientId);
    return client ? client.name : '';
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return 'bg-red-500';
      case 'medium':
        return 'bg-amber-500';
      case 'low':
        return 'bg-green-500';
      default:
        return 'bg-slate-500';
    }
  };

  return (
    <Card className="col-span-2">
      <CardHeader>
        <CardTitle>Pr√≥ximas Tarefas</CardTitle>
        <CardDescription>
          Tarefas pendentes mais urgentes
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {upcomingTasks.map((task) => (
            <div key={task.id} className="flex items-start space-x-3">
              <Checkbox id={`task-${task.id}`} />
              <div className="space-y-1 flex-1">
                <label
                  htmlFor={`task-${task.id}`}
                  className="font-medium text-sm cursor-pointer"
                >
                  {task.title}
                </label>
                <div className="flex flex-wrap items-center gap-2 text-xs">
                  <Badge variant="outline" className="flex items-center gap-1 py-0 px-2">
                    <span
                      className={cn("w-2 h-2 rounded-full", getPriorityColor(task.priority))}
                    />
                    <span>
                      {task.priority === 'high' && 'Alta'}
                      {task.priority === 'medium' && 'M√©dia'}
                      {task.priority === 'low' && 'Baixa'}
                    </span>
                  </Badge>
                  {task.clientId && (
                    <span className="text-muted-foreground">
                      {getClientName(task.clientId)}
                    </span>
                  )}
                </div>
                <div className="text-xs text-muted-foreground">
                  Vence em {format(task.dueDate, "dd 'de' MMMM", { locale: ptBR })}
                </div>
              </div>
              <div className="text-xs text-muted-foreground whitespace-nowrap">
                {task.assignedTo}
              </div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
}

