    appSecret?: string;
    accessToken?: string;
    pageId?: string;
    phoneNumberId?: string;
    businessAccountId?: string;
  };
  ultimaConexao?: Date;
}

export function ChatIntegrations() {
  const { toast } = useToast();

  const [integracoes, setIntegracoes] = useState<IntegracaoChat[]>([
