src/components/integrations/WebhookSetti.content:
/**
 * Componente para configura√ß√£o de Webhooks
 * Permite gerenciar URLs de webhook para integra√ß√µes com plataformas externas
 */
import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Badge } from '@/components/ui/badge';
import { Form, FormField, FormItem, FormLabel, FormControl, FormMessage, FormDescription } from '@/components/ui/form';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Separator } from '@/components/ui/separator';
import { Trash2, Plus, Link, Check, X, Globe } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import { useToast } from '@/hooks/use-toast';

/**
 * Interface para configura√ß√£o de webhook
 */
interface ConfiguracaoWebhook {
  id: string;
  nome: string;
  url: string;
  eventos: string[];
  ativo: boolean;
  dataCriacao: Date;
  ultimaAtualizacao: Date;
  ultimoDisparo?: Date;
}

/**
 * Schema de valida√ß√£o para formul√°rio de webhook
 */
const webhookSchema = z.object({
  nome: z.string().min(3, 'Nome deve ter pelo menos 3 caracteres'),
  url: z.string().url('URL deve ser v√°lida'),
  eventos: z.array(z.string()).min(1, 'Selecione pelo menos um evento'),
  ativo: z.boolean(),
});

type WebhookFormData = z.infer<typeof webhookSchema>;

/**
 * Componente principal de configura√ß√£o de webhooks
 */
export function WebhookSettings() {
  const { toast } = useToast();

  // Estados
  const [webhooks, setWebhooks] = useState<ConfiguracaoWebhook[]>([
    {
      id: '1',
      nome: 'Zapier - Novos Leads',
      url: 'https://hooks.zapier.com/hooks/catch/123456/abc123/',
      eventos: ['novo_lead', 'oportunidade_criada'],
      ativo: true,
      dataCriacao: new Date('2024-01-15'),
      ultimaAtualizacao: new Date('2024-01-20'),
      ultimoDisparo: new Date('2024-01-25T10:30:00'),
    },
    {
      id: '2',
      nome: 'Make.com - Vendas Fechadas',
      url: 'https://hook.integromat.com/abc123def456',
      eventos: ['venda_fechada'],
      ativo: false,
      dataCriacao: new Date('2024-01-10'),
      ultimaAtualizacao: new Date('2024-01-15'),
    },
  ]);

  const [editandoId, setEditandoId] = useState<string | null>(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);

  // Eventos dispon√≠veis para webhook
  const eventosDisponiveis = [
    { id: 'novo_lead', nome: 'Novo Lead Criado' },
    { id: 'oportunidade_criada', nome: 'Oportunidade Criada' },
    { id: 'oportunidade_atualizada', nome: 'Oportunidade Atualizada' },
    { id: 'venda_fechada', nome: 'Venda Fechada' },
    { id: 'cliente_criado', nome: 'Cliente Criado' },
    { id: 'tarefa_criada', nome: 'Tarefa Criada' },
  ];

  // Formul√°rio
  const form = useForm<WebhookFormData>({
    resolver: zodResolver(webhookSchema),
    defaultValues: {
      nome: '',
      url: '',
      eventos: [],
      ativo: true,
    },
  });

  /**
   * Submete o formul√°rio de webhook
   */
  const onSubmit = (data: WebhookFormData) => {
    try {
      if (editandoId) {
        // Atualizar webhook existente
        setWebhooks(prev => prev.map(webhook => 
          webhook.id === editandoId 
            ? { 
                ...webhook, 
                nome: data.nome,
                url: data.url,
                eventos: data.eventos,
                ativo: data.ativo,
                ultimaAtualizacao: new Date() 
              }
            : webhook
        ));

        toast({
          title: "Webhook atualizado",
          description: "As configura√ß√µes foram salvas com sucesso.",
        });
      } else {
        // Criar novo webhook
        const novoWebhook: ConfiguracaoWebhook = {
          id: Date.now().toString(),
          nome: data.nome,
          url: data.url,
          eventos: data.eventos,
          ativo: data.ativo,
          dataCriacao: new Date(),
          ultimaAtualizacao: new Date(),
        };

        setWebhooks(prev => [...prev, novoWebhook]);

        toast({
          title: "Webhook criado",
          description: "Nova configura√ß√£o de webhook foi adicionada.",
        });
      }

      // Reset do formul√°rio
      form.reset();
      setEditandoId(null);
      setMostrarFormulario(false);
    } catch (error) {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel salvar as configura√ß√µes.",
        variant: "destructive",
      });
    }
  };

  /**
   * Inicia edi√ß√£o de webhook
   */
  const iniciarEdicao = (webhook: ConfiguracaoWebhook) => {
    setEditandoId(webhook.id);
    setMostrarFormulario(true);
    form.reset({
      nome: webhook.nome,
      url: webhook.url,
      eventos: webhook.eventos,
      ativo: webhook.ativo,
    });
  };

  /**
   * Remove webhook
   */
  const removerWebhook = (id: string) => {
    setWebhooks(prev => prev.filter(webhook => webhook.id !== id));
    toast({
      title: "Webhook removido",
      description: "A configura√ß√£o foi exclu√≠da com sucesso.",
    });
  };

  /**
   * Alterna status ativo/inativo do webhook
   */
  const alternarStatus = (id: string) => {
    setWebhooks(prev => prev.map(webhook => 
      webhook.id === id 
        ? { ...webhook, ativo: !webhook.ativo, ultimaAtualizacao: new Date() }
        : webhook
    ));
  };

  /**
   * Testa webhook enviando requisi√ß√£o
   */
  const testarWebhook = async (webhook: ConfiguracaoWebhook) => {
    try {
      const response = await fetch(webhook.url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        mode: 'no-cors',
        body: JSON.stringify({
          evento: 'teste',
          timestamp: new Date().toISOString(),
          origem: 'VisionCRM',
          dados: {
            teste: true,
            webhook_id: webhook.id,
          },
        }),
      });

      // Atualizar √∫ltimo disparo
      setWebhooks(prev => prev.map(w => 
        w.id === webhook.id 
          ? { ...w, ultimoDisparo: new Date() }
          : w
      ));

      toast({
        title: "Teste enviado",
        description: "O webhook de teste foi disparado com sucesso.",
      });
    } catch (error) {
      toast({
        title: "Erro no teste",
        description: "N√£o foi poss√≠vel enviar o teste para o webhook.",
        variant: "destructive",
      });
    }
  };

  /**
   * Formata data para exibi√ß√£o
   */
  const formatarData = (data: Date) => {
    return new Intl.DateTimeFormat('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    }).format(data);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Configura√ß√£o de Webhooks</h2>
          <p className="text-muted-foreground">
            Configure URLs de webhook para integrar com Zapier, Make.com e outras plataformas.
          </p>
        </div>
        <Button onClick={() => setMostrarFormulario(true)}>
          <Plus className="mr-2 h-4 w-4" />
          Novo Webhook
        </Button>
      </div>

      {/* Formul√°rio de cria√ß√£o/edi√ß√£o */}
      {mostrarFormulario && (
        <Card>
          <CardHeader>
            <CardTitle>
              {editandoId ? 'Editar Webhook' : 'Novo Webhook'}
            </CardTitle>
            <CardDescription>
              Configure os detalhes do webhook para integra√ß√£o externa.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="nome"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Nome do Webhook</FormLabel>
                      <FormControl>
                        <Input placeholder="Ex: Zapier - Novos Leads" {...field} />
                      </FormControl>
                      <FormDescription>
                        Um nome descritivo para identificar este webhook.
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="url"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>URL do Webhook</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="https://hooks.zapier.com/hooks/catch/..." 
                          {...field} 
                        />
                      </FormControl>
                      <FormDescription>
                        URL fornecida pela plataforma de integra√ß√£o (Zapier, Make.com, etc).
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="eventos"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Eventos que Disparam o Webhook</FormLabel>
                      <div className="grid grid-cols-2 gap-2">
                        {eventosDisponiveis.map((evento) => (
                          <div key={evento.id} className="flex items-center space-x-2">
                            <input
                              type="checkbox"
                              id={evento.id}
                              checked={field.value.includes(evento.id)}
                              onChange={(e) => {
                                const novoEventos = e.target.checked
                                  ? [...field.value, evento.id]
                                  : field.value.filter(id => id !== evento.id);
                                field.onChange(novoEventos);
                              }}
                              className="rounded border-gray-300"
                            />
                            <Label htmlFor={evento.id} className="text-sm">
                              {evento.nome}
                            </Label>
                          </div>
                        ))}
                      </div>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="ativo"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <div>
                        <FormLabel>Webhook Ativo</FormLabel>
                        <FormDescription>
                          Quando ativo, o webhook ser√° disparado automaticamente.
                        </FormDescription>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <div className="flex justify-end space-x-2">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => {
                      setMostrarFormulario(false);
                      setEditandoId(null);
                      form.reset();
                    }}
                  >
                    Cancelar
                  </Button>
                  <Button type="submit">
                    {editandoId ? 'Atualizar' : 'Criar'} Webhook
                  </Button>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      )}

      {/* Lista de webhooks */}
      <div className="space-y-4">
        {webhooks.map((webhook) => (
          <Card key={webhook.id}>
            <CardContent className="p-6">
              <div className="flex items-start justify-between">
                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <h3 className="font-medium">{webhook.nome}</h3>
                    <Badge variant={webhook.ativo ? "default" : "secondary"}>
                      {webhook.ativo ? 'Ativo' : 'Inativo'}
                    </Badge>
                  </div>

                  <div className="flex items-center space-x-2 text-sm text-muted-foreground">
                    <Link className="h-4 w-4" />
                    <span className="font-mono">{webhook.url}</span>
                  </div>

                  <div className="flex flex-wrap gap-1">
                    {webhook.eventos.map((eventoId) => {
                      const evento = eventosDisponiveis.find(e => e.id === eventoId);
                      return (
                        <Badge key={eventoId} variant="outline" className="text-xs">
                          {evento?.nome || eventoId}
                        </Badge>
                      );
                    })}
                  </div>

                  <div className="text-xs text-muted-foreground space-y-1">
                    <div>Criado em: {formatarData(webhook.dataCriacao)}</div>
                    <div>√öltima atualiza√ß√£o: {formatarData(webhook.ultimaAtualizacao)}</div>
                    {webhook.ultimoDisparo && (
                      <div>√öltimo disparo: {formatarData(webhook.ultimoDisparo)}</div>
                    )}
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    checked={webhook.ativo}
                    onCheckedChange={() => alternarStatus(webhook.id)}
                  />
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => testarWebhook(webhook)}
                  >
                    <Globe className="h-4 w-4 mr-1" />
                    Testar
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => iniciarEdicao(webhook)}
                  >
                    Editar
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => removerWebhook(webhook.id)}
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}

        {webhooks.length === 0 && (
          <Card>
            <CardContent className="p-8 text-center">
              <div className="space-y-2">
                <h3 className="font-medium">Nenhum webhook configurado</h3>
                <p className="text-sm text-muted-foreground">
                  Adicione seu primeiro webhook para come√ßar a integrar com plataformas externas.
                </p>
                <Button onClick={() => setMostrarFormulario(true)}>
                  <Plus className="mr-2 h-4 w-4" />
                  Criar Primeiro Webhook
                </Button>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

src/components/layout/AppLayout.tsx.content:
/**
 * Componente de layout principal da aplica√ß√£o
 * Gerencia a estrutura b√°sica da interface, incluindo cabe√ßalho, barra lateral e √°rea de conte√∫do
 * Implementa visualiza√ß√£o responsiva para dispositivos m√≥veis
 */
import React, { useState, useEffect } from 'react';
import { Sidebar } from './Sidebar';
import { Toaster } from '@/components/ui/toaster';
import { Toaster as Sonner } from '@/components/ui/sonner';
import Header from './Header';
import { ThemeProvider } from '@/contexts/ThemeContext';
import { useIsMobile } from '@/hooks/use-mobile';

interface AppLayoutProps {
  children: React.ReactNode;
}

/**
 * Renderiza o layout principal da aplica√ß√£o
 * @param children - Conte√∫do principal a ser renderizado dentro do layout
 */
const AppLayout: React.FC<AppLayoutProps> = ({ children }) => {
  const isMobile = useIsMobile(); // Detecta se √© dispositivo m√≥vel
  const [sidebarOpen, setSidebarOpen] = useState(!isMobile); // Inicialmente fecha em dispositivos m√≥veis

  /**
   * Efeito para ajustar automaticamente o estado da barra lateral quando muda de desktop para m√≥vel ou vice-versa
   */
  useEffect(() => {
    if (isMobile) {
      setSidebarOpen(false); // Fecha automaticamente em dispositivos m√≥veis
    }
  }, [isMobile]);

  /**
   * Alterna a visibilidade da barra lateral
   */
  const alternarSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  /**
   * Fecha a sidebar quando um item √© clicado (para m√≥vel)
   */
  const fecharSidebar = () => {
    if (isMobile) {
      setSidebarOpen(false);
    }
  };

  return (
    <ThemeProvider>
      <div className="min-h-screen flex flex-col bg-background relative">
        {/* Simplified background */}
        <div className="fixed inset-0 pointer-events-none z-0">
          <div className="absolute top-20 right-20 w-96 h-96 bg-primary/5 rounded-full blur-3xl opacity-50"></div>
          <div className="absolute bottom-20 left-20 w-64 h-64 bg-muted/30 rounded-full blur-2xl opacity-30"></div>
        </div>

        <Header toggleSidebar={alternarSidebar} />
        <div className="flex flex-1 overflow-hidden relative z-10">
          {/* Overlay para dispositivos m√≥veis */}
          {sidebarOpen && isMobile && (
            <div
              className="fixed inset-0 bg-black/50 z-20 md:hidden"
              onClick={fecharSidebar}
            />
          )}

          <Sidebar isOpen={sidebarOpen} onItemClick={fecharSidebar} />
          <main 
            className={`
              flex-1 overflow-auto transition-all duration-500 ease-in-out
              ${sidebarOpen && !isMobile ? 'md:pl-72' : ''}
              ${isMobile ? 'p-4' : 'p-6'}
            `}
          >
            <div className={`container mx-auto ${isMobile ? 'px-2' : 'px-4'}`}>
              <div className={`bg-card/50 backdrop-blur-sm border border-border/50 rounded-xl min-h-[calc(100vh-200px)] shadow-sm ${isMobile ? 'p-4' : 'p-6'}`}>
                {children}
              </div>
            </div>
          </main>
        </div>
        <Toaster />
        <Sonner />
      </div>
    </ThemeProvider>
  );
};

export default AppLayout;

