          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis tickFormatter={(value) => `R$${value/1000}k`} />
          <Tooltip formatter={(value: number) => [formatCurrency(value), ""]} />
          <Legend />
          <Bar dataKey="basicPlus" name="Plano B√°sico+" stackId="a" fill="#4ade80" radius={[4, 4, 0, 0]} />
          <Bar dataKey="professional" name="Plano Profissional" stackId="a" fill="#3b82f6" />
          <Bar dataKey="enterprise" name="Plano Enterprise" stackId="a" fill="#8b5cf6" />
        </BarChart>
      )}
    </ResponsiveContainer>
  );
}

src/components/sales/KanbanBoard.tsx.content:
import React, { useState, useEffect } from "react";
import type { DropResult } from "@hello-pangea/dnd";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Plus,
  Calendar,
  DollarSign,
  User,
  Users,
  Send,
  Handshake,
  Trophy,
  XCircle,
  Edit2,
  Trash2,
} from "lucide-react";
import { NewOpportunityDialog } from "./NewOpportunityDialog";
import {
  buscarOportunidades,
  atualizarOportunidade,
  excluirOportunidade,
  buscarClientes,
} from "@/services/api";
import { useToast } from "@/hooks/use-toast";

interface Oportunidade {
  id: string;
  titulo: string;
  descricao: string;
  valor: number;
  status: string;
  probabilidade: number;
  dataPrevisao?: string;
  cliente: {
    id: string;
    nome: string;
    nomeEmpresa?: string;
    empresa?: { nome: string };
  };
  usuario: {
    id?: string;
    nome: string;
  };
  createdAt: string;
}

interface Cliente {
  id: string;
  nome: string;
  nomeEmpresa?: string;
}

const etapasComerciais = [
  { id: "LEAD", nome: "Lead Recebido", icone: Users, cor: "bg-blue-500" },
  { id: "QUALIFICADO", nome: "Qualificado", icone: User, cor: "bg-indigo-500" },
  { id: "PROPOSTA", nome: "Proposta", icone: Send, cor: "bg-purple-500" },
  {
    id: "NEGOCIACAO",
    nome: "Negocia√ß√£o",
    icone: Handshake,
    cor: "bg-yellow-500",
  },
  { id: "GANHO", nome: "Ganho", icone: Trophy, cor: "bg-green-500" },
  { id: "PERDIDO", nome: "Perdido", icone: XCircle, cor: "bg-red-500" },
] as const;

export const KanbanBoard: React.FC = () => {
  const [oportunidades, setOportunidades] = useState<
    Record<string, Oportunidade[]>
  >({});
  const [dialogAberto, setDialogAberto] = useState(false);
  const [clientes, setClientes] = useState<Cliente[]>([]);
  const [loading, setLoading] = useState(true);
  const [pipelines, setPipelines] = useState([
    { id: "1", nome: "Pipeline Principal", ativo: true },
    { id: "2", nome: "Pipeline Produtos", ativo: false },
    { id: "3", nome: "Pipeline Servi√ßos", ativo: false },
  ]);
  const [pipelineAtivo, setPipelineAtivo] = useState("1");
  const { toast } = useToast();

  useEffect(() => {
    carregarDados();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const carregarDados = async () => {
    try {
      setLoading(true);

      const [oportunidadesData, clientesData] = await Promise.all([
        buscarOportunidades(),
        buscarClientes(),
      ]);

      const oportunidadesOrganizadas: Record<string, Oportunidade[]> = {};
      etapasComerciais.forEach(
        (etapa) => (oportunidadesOrganizadas[etapa.id] = []),
      );

      // Card de exemplo (n√£o persiste)
      oportunidadesOrganizadas["LEAD"] = [
        {
          id: "exemplo-1",
          titulo: "Oportunidade de Exemplo",
          descricao: "Descri√ß√£o da oportunidade de exemplo para demonstra√ß√£o",
          valor: 15000,
          status: "LEAD",
          probabilidade: 20,
          dataPrevisao: new Date(
            Date.now() + 30 * 24 * 60 * 60 * 1000,
          ).toISOString(),
          cliente: {
            id: "cliente-exemplo",
            nome: "Cliente Exemplo",
            nomeEmpresa: "Empresa Exemplo",
          },
          usuario: { id: "user-exemplo", nome: "Vendedor Exemplo" },
          createdAt: new Date().toISOString(),
        },
      ];

      if (Array.isArray(oportunidadesData)) {
        oportunidadesData.forEach((o: any) => {
          const status: string = o.status || "LEAD";
          if (!oportunidadesOrganizadas[status]) return;

          const oportunidadeMapeada: Oportunidade = {
            id: String(o.id),
            titulo: o.titulo,
            descricao: o.descricao || "",
            valor: Number(o.valor || 0),
            status,
            probabilidade: Number(o.probabilidade || 0),
            dataPrevisao: o.dataPrevisao,
            cliente: {
              id: o.cliente?.id ? String(o.cliente.id) : "",
              nome: o.cliente?.nome || "Cliente n√£o identificado",
              nomeEmpresa:
                o.cliente?.nomeEmpresa || o.cliente?.empresa?.nome || "",
              empresa: o.cliente?.empresa,
            },
            usuario: {
              id: o.usuario?.id || o.usuarioId,
              nome: o.usuario?.nome || "Usu√°rio n√£o identificado",
            },
            createdAt: o.createdAt || o.criadoEm || new Date().toISOString(),
          };

          oportunidadesOrganizadas[status].push(oportunidadeMapeada);
        });
      }

      setOportunidades(oportunidadesOrganizadas);

      // aceitar tanto array quanto { clientes: [] }
      const listaClientes = Array.isArray(clientesData)
        ? clientesData
        : Array.isArray(clientesData?.clientes)
          ? clientesData.clientes
          : [];
      setClientes(listaClientes);
    } catch (error) {
      console.error("Erro ao carregar dados:", error);
      toast({
        title: "Erro",
        description: "Erro ao carregar oportunidades",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const aoTerminarArrasto = async (resultado: DropResult) => {
    const { destination, source, draggableId } = resultado;
    if (!destination) return;
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    )
      return;

    try {
      const origemLista = oportunidades[source.droppableId] || [];
      const oportunidadeArrastada = origemLista.find(
        (op) => op.id === draggableId,
      );
      if (!oportunidadeArrastada) return;

      const novasOportunidades = structuredClone(oportunidades);

      // remover da coluna origem
      novasOportunidades[source.droppableId] = (
        novasOportunidades[source.droppableId] || []
      ).filter((op) => op.id !== draggableId);

      // inserir na coluna destino na posi√ß√£o
      const destinoLista = [
        ...(novasOportunidades[destination.droppableId] || []),
      ];
      const oportunidadeAtualizada: Oportunidade = {
        ...oportunidadeArrastada,
        status: destination.droppableId,
      };
      destinoLista.splice(destination.index, 0, oportunidadeAtualizada);
      novasOportunidades[destination.droppableId] = destinoLista;

      setOportunidades(novasOportunidades);

      if (draggableId !== "exemplo-1") {
        await atualizarOportunidade(draggableId, {
          status: destination.droppableId,
        });
      }

      const etapaDestino = etapasComerciais.find(
        (e) => e.id === destination.droppableId,
      );
      toast({
        title: "Oportunidade movida",
        description: `Movida para "${etapaDestino?.nome}" com sucesso`,
      });
    } catch (error) {
      console.error("Erro ao mover oportunidade:", error);
      await carregarDados();
      toast({
        title: "Erro",
        description: "Erro ao mover oportunidade. Tente novamente.",
        variant: "destructive",
      });
    }
  };

  const adicionarOportunidade = () => {
    carregarDados();
    setDialogAberto(false);
  };

  const excluirOportunidadeHandler = async (id: string) => {
    if (confirm("Tem certeza que deseja excluir esta oportunidade?")) {
      try {
        await excluirOportunidade(id);
        await carregarDados();
        toast({
          title: "Sucesso",
          description: "Oportunidade exclu√≠da com sucesso",
        });
      } catch (error) {
        console.error("Erro ao excluir oportunidade:", error);
        toast({
          title: "Erro",
          description: "Erro ao excluir oportunidade",
          variant: "destructive",
        });
      }
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto" />
          <p className="mt-2 text-muted-foreground">
            Carregando oportunidades...
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      {/* Seletor de Pipeline */}
      <div className="flex items-center justify-between p-4 border-b border-border/50">
        <div className="flex items-center gap-4">
          <select
            value={pipelineAtivo}
            onChange={(e) => setPipelineAtivo(e.target.value)}
            className="bg-background border border-border rounded-lg px-3 py-2 text-foreground"
          >
            {pipelines.map((pipeline) => (
              <option key={pipeline.id} value={pipeline.id}>
                {pipeline.nome}
              </option>
            ))}
          </select>
          <Button
            variant="outline"
            size="sm"
            onClick={() => {
              const nome = prompt("Nome do novo pipeline:");
              if (nome) {
                const novoId = String(pipelines.length + 1);
                setPipelines([
                  ...pipelines,
                  { id: novoId, nome, ativo: false },
                ]);
              }
            }}
          >
            <Plus className="w-4 h-4 mr-2" />
            Novo Pipeline
          </Button>
        </div>
        <Button onClick={() => setDialogAberto(true)}>
          <Plus className="w-4 h-4 mr-2" />
          Nova Oportunidade
        </Button>
      </div>

      <div className="flex-1 overflow-x-auto">
        <DragDropContext onDragEnd={aoTerminarArrasto}>
          <div className="flex gap-4 p-4 min-w-max">
            {etapasComerciais.map((etapa) => {
              const items = oportunidades[etapa.id] || [];
              const IconeEtapa = etapa.icone;

              const totalColuna = items.reduce(
                (total, item) => total + (item.valor || 0),
                0,
              );
              const mediaProb =
                items.length > 0
                  ? Math.round(
                    items.reduce(
                      (total, item) => total + (item.probabilidade || 0),
                      0,
                    ) / items.length,
                  )
                  : 0;

              return (
                <Droppable key={etapa.id} droppableId={etapa.id}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.droppableProps}
                      className={`w-72 transition-all duration-200 ${snapshot.isDraggingOver
                        ? 'bg-primary/5 rounded-lg ring-2 ring-primary/20'
                        : ''
                        }`}
                    >
                      <div className="glass-card rounded-lg p-3 mb-4">
                        <div className="flex items-center justify-between mb-3">
                          <div className="flex items-center gap-2">
                            <div
                              className={`p-2 rounded-lg ${etapa.cor} bg-opacity-10`}
                            >
                              <IconeEtapa
                                className={`h-4 w-4 ${etapa.cor.replace("bg-", "text-")}`}
                              />
                            </div>
                            <div>
                              <h3 className="font-medium text-foreground text-sm">
                                {etapa.nome}
                              </h3>
                              <p className="text-xs text-muted-foreground">
                                {totalColuna.toLocaleString("pt-BR", {
                                  style: "currency",
                                  currency: "BRL",
                                })}
                              </p>
                            </div>
                          </div>
                          <div className="flex flex-col items-end gap-1">
                            <Badge variant="secondary" className="text-xs">
                              {items.length}
                            </Badge>
                            {items.length > 0 && (
                              <div className="text-xs text-muted-foreground">
                                M√©dia: {mediaProb}%
                              </div>
                            )}
                          </div>
                        </div>
                      </div>

                      <div className="space-y-3 h-[calc(100vh-200px)] overflow-y-auto">
                        {items.map((oportunidade, index) => (
                          <Draggable
                            key={oportunidade.id}
                            draggableId={oportunidade.id}
                            index={index}
                          >
                            {(prov, snap) => (
                              <Card
                                ref={prov.innerRef}
                                {...prov.draggableProps}
                                {...prov.dragHandleProps}
                                className={`cursor-grab glass-card kanban-card transition-all duration-200 hover:shadow-md ${snap.isDragging
                                  ? "shadow-2xl opacity-95 ring-2 ring-primary/20 !z-[9999] rotate-2 scale-105"
                                  : "hover:shadow-lg"
                                  }`}
                                style={{
                                  ...prov.draggableProps.style,
                                }}
                              >
                                <CardHeader className="pb-2">
                                  <div className="flex items-center justify-between">
                                    <CardTitle className="text-sm font-medium truncate">
                                      {oportunidade.titulo}
                                    </CardTitle>
                                    <div className="flex gap-1">
                                      <Button
                                        variant="ghost"
                                        size="icon"
                                        className="h-6 w-6"
                                      >
                                        <Edit2 className="w-3 h-3" />
                                      </Button>
                                      <Button
                                        variant="ghost"
                                        size="icon"
                                        className="h-6 w-6"
                                        onClick={(e) => {
                                          e.stopPropagation();
                                          excluirOportunidadeHandler(
                                            oportunidade.id,
                                          );
                                        }}
                                      >
                                        <Trash2 className="w-3 h-3 text-destructive" />
                                      </Button>
                                    </div>
                                  </div>
                                </CardHeader>
                                <CardContent className="pt-0">
                                  <p className="text-xs text-muted-foreground mb-3 line-clamp-2">
                                    {oportunidade.descricao}
                                  </p>

                                  <div className="space-y-2">
                                    <div className="flex items-center gap-2 text-xs">
                                      <DollarSign className="w-3 h-3 text-emerald-500" />
                                      <span className="font-medium text-emerald-600">
                                        {oportunidade.valor
                                          ? oportunidade.valor.toLocaleString(
                                            "pt-BR",
                                            {
                                              style: "currency",
                                              currency: "BRL",
                                            },
                                          )
                                          : "R$ 0,00"}
                                      </span>
                                    </div>

                                    <div className="flex items-center gap-2 text-xs text-muted-foreground">
                                      <User className="w-3 h-3" />
                                      <span>{oportunidade.cliente.nome}</span>
                                    </div>

                                    {oportunidade.dataPrevisao && (
                                      <div className="flex items-center gap-2 text-xs text-muted-foreground">
                                        <Calendar className="w-3 h-3" />
                                        <span>
                                          {new Date(
                                            oportunidade.dataPrevisao,
                                          ).toLocaleDateString("pt-BR")}
                                        </span>
                                      </div>
                                    )}
                                  </div>

                                  <div className="mt-3 pt-3 border-t border-border/50">
                                    <div className="flex items-center justify-between">
                                      <Badge
                                        variant="outline"
                                        className="text-xs"
                                      >
                                        {oportunidade.usuario.nome}
                                      </Badge>
                                      {!!oportunidade.probabilidade && (
                                        <Badge
                                          variant="secondary"
                                          className="text-xs"
                                        >
                                          {oportunidade.probabilidade}%
                                        </Badge>
                                      )}
                                    </div>
                                  </div>
                                </CardContent>
                              </Card>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </div>
                    </div>
                  )}
                </Droppable>
              );
            })}
          </div>
        </DragDropContext>
      </div>

      <NewOpportunityDialog
        open={dialogAberto}
        onOpenChange={setDialogAberto}
        onSubmit={adicionarOportunidade}
        clientes={clientes}
      />
    </div>
  );
};

src/components/sales/NewOpportunityDialo.content:
import React, { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { criarOportunidade, buscarDadosUsuario } from '@/services/api';
import { useToast } from '@/hooks/use-toast';

interface Cliente {
  id: string;
  nome: string;
  nomeEmpresa?: string;
}

interface NewOpportunityDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSubmit: (oportunidade: any) => void;
  clientes: Cliente[];
}

export const NewOpportunityDialog: React.FC<NewOpportunityDialogProps> = ({
  open,
  onOpenChange,
  onSubmit,
  clientes
}) => {
  const [formData, setFormData] = useState({
    titulo: '',
    descricao: '',
    valor: '',
    clienteId: '',
    probabilidade: '',
    dataFechamentoEsperada: ''
  });
  const [loading, setLoading] = useState(false);
  const [usuarioAtual, setUsuarioAtual] = useState<any>(null);
  const { toast } = useToast();

  useEffect(() => {
    if (open) {
      carregarUsuario();
    }
  }, [open]);

  const carregarUsuario = async () => {
    try {
      const userData = await buscarDadosUsuario();
      setUsuarioAtual(userData);
    } catch (error) {
      console.error('Erro ao carregar dados do usu√°rio:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!usuarioAtual) {
      toast({
        title: "Erro",
        description: "Dados do usu√°rio n√£o carregados",
        variant: "destructive",
      });
      return;
    }

    setLoading(true);

    try {
      const oportunidadeData = {
        titulo: formData.titulo,
        descricao: formData.descricao,
        valor: parseFloat(formData.valor) || 0,
        clienteId: formData.clienteId,
        responsavelId: usuarioAtual.id,
        estagio: 'LEAD',
        probabilidade: parseInt(formData.probabilidade) || 0,
        dataFechamentoEsperada: formData.dataFechamentoEsperada || null
      };

      await criarOportunidade(oportunidadeData);

      toast({
        title: "Sucesso",
        description: "Oportunidade criada com sucesso",
      });

      onSubmit(oportunidadeData);

      // Reset form
      setFormData({
        titulo: '',
        descricao: '',
        valor: '',
        clienteId: '',
        probabilidade: '',
        dataFechamentoEsperada: ''
      });
    } catch (error) {
      console.error('Erro ao criar oportunidade:', error);
      toast({
        title: "Erro",
        description: "Erro ao criar oportunidade",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="glass-card animate-scale-in max-w-md">
        <DialogHeader>
          <DialogTitle>Nova Oportunidade</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="titulo">T√≠tulo</Label>
            <Input
              id="titulo"
              value={formData.titulo}
              onChange={(e) => setFormData({ ...formData, titulo: e.target.value })}
              required
              className="glass-card"
            />
          </div>

          <div>
            <Label htmlFor="cliente">Cliente</Label>
            <Select
              value={formData.clienteId}
              onValueChange={(value) => setFormData({ ...formData, clienteId: value })}
            >
              <SelectTrigger className="glass-card">
                <SelectValue placeholder="Selecione um cliente" />
              </SelectTrigger>
              <SelectContent>
                {clientes.map((cliente) => (
                  <SelectItem key={cliente.id} value={cliente.id}>
                    {cliente.nome} {cliente.nomeEmpresa && `(${cliente.nomeEmpresa})`}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="valor">Valor (R$)</Label>
            <Input
              id="valor"
              type="number"
              step="0.01"
              value={formData.valor}
              onChange={(e) => setFormData({ ...formData, valor: e.target.value })}
              className="glass-card"
            />
          </div>

          <div>
            <Label htmlFor="probabilidade">Probabilidade (%)</Label>
            <Input
              id="probabilidade"
              type="number"
              min="0"
              max="100"
              value={formData.probabilidade}
              onChange={(e) => setFormData({ ...formData, probabilidade: e.target.value })}
              className="glass-card"
            />
          </div>

          <div>
            <Label htmlFor="dataFechamentoEsperada">Data de Fechamento Esperada</Label>
            <Input
              id="dataFechamentoEsperada"
              type="date"
              value={formData.dataFechamentoEsperada}
              onChange={(e) => setFormData({ ...formData, dataFechamentoEsperada: e.target.value })}
              className="glass-card"
            />
          </div>

          <div>
            <Label htmlFor="descricao">Descri√ß√£o</Label>
            <Textarea
              id="descricao"
              value={formData.descricao}
              onChange={(e) => setFormData({ ...formData, descricao: e.target.value })}
              className="glass-card"
              rows={3}
            />
          </div>

          <div className="flex justify-end gap-2 pt-4">
            <Button type="button" variant="outline" onClick={() => onOpenChange(false)}>
              Cancelar
            </Button>
            <Button type="submit" disabled={loading} className="transition-all hover:scale-105">
              {loading ? 'Criando...' : 'Criar Oportunidade'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
};

src/components/settings/LanguageSettings.content:
/**
 * Componente para configura√ß√£o de idioma
 * Permite ao usu√°rio selecionar o idioma padr√£o da plataforma
 */
import React from 'react';
import { z } from "zod";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { toast } from "sonner";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { atualizarIdioma, buscarIdioma } from "@/services/api";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Loader2, Globe } from "lucide-react";

/**
 * Interface para op√ß√µes de idioma
 */
interface OpcaoIdioma {
  valor: string;
  rotulo: string;
}

/**
 * Op√ß√µes de idioma dispon√≠veis
 */
const opcoesDeLinguagem: OpcaoIdioma[] = [
  { valor: "pt-BR", rotulo: "Portugu√™s (Brasil)" },
  { valor: "en-US", rotulo: "English (US)" },
];

/**
 * Esquema de valida√ß√£o para o formul√°rio de idioma
 */
const formSchema = z.object({
  idioma: z.string({
    required_error: "Por favor selecione um idioma",
  }),
});

type FormValues = z.infer<typeof formSchema>;

/**
 * Formul√°rio para configura√ß√£o de idioma
 */
export function LanguageSettings() {
  // Busca configura√ß√£o de idioma atual
  const { data: idiomaAtual, isLoading } = useQuery({
    queryKey: ['idioma-usuario'],
    queryFn: buscarIdioma,
  });

  // Configura√ß√£o do formul√°rio
  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      idioma: "pt-BR",
    },
    values: idiomaAtual ? { idioma: idiomaAtual.codigo } : undefined,
  });

  // Muta√ß√£o para atualizar idioma
  const mutation = useMutation({
    mutationFn: (values: FormValues) => atualizarIdioma(values.idioma),
    onSuccess: () => {
      toast.success("Idioma atualizado com sucesso!");
    },
    onError: () => {
      toast.error("Erro ao atualizar idioma. Tente novamente.");
    },
  });

  /**
   * Fun√ß√£o para enviar o formul√°rio
   * @param values - Valores do formul√°rio
   */
  function onSubmit(values: FormValues) {
    mutation.mutate(values);
  }

  if (isLoading) {
    return (
      <div className="flex justify-center items-center py-4">
        <Loader2 className="h-6 w-6 animate-spin text-primary" />
      </div>
    );
  }

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
        <FormField
          control={form.control}
          name="idioma"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Idioma padr√£o</FormLabel>
              <div className="relative">
                <Globe className="absolute left-2 top-2.5 h-5 w-5 text-muted-foreground" />
                <FormControl>
                  <Select 
                    onValueChange={field.onChange} 
                    defaultValue={field.value}
                  >
                    <SelectTrigger className="pl-9 w-full">
                      <SelectValue placeholder="Selecione um idioma" />
                    </SelectTrigger>
                    <SelectContent>
                      {opcoesDeLinguagem.map((opcao) => (
                        <SelectItem key={opcao.valor} value={opcao.valor}>
                          {opcao.rotulo}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </FormControl>
              </div>
              <FormMessage />
            </FormItem>
          )}
        />

        <Button 
          type="submit" 
          className="w-full sm:w-auto"
          disabled={mutation.isPending}
        >
          {mutation.isPending ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Salvando...
            </>
          ) : "Salvar prefer√™ncia"}
        </Button>
      </form>
    </Form>
  );
}

