src/components/settings/ProfileSettingsF.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Switch } from '@/components/ui/switch';
import { useToast } from '@/hooks/use-toast';
import { buscarPerfis, atualizarPerfis } from '@/services/api';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Plus, Trash2 } from 'lucide-react';

interface Perfil {
  id: string;
  nome: string;
  ativo: boolean;
}

export const ProfileSettingsForm: React.FC = () => {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [perfis, setPerfis] = useState<Perfil[]>([]);

  const { data: perfisData } = useQuery({
    queryKey: ['perfis-usuario'],
    queryFn: buscarPerfis
  });

  const mutationAtualizar = useMutation({
    mutationFn: atualizarPerfis,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['perfis-usuario'] });
      toast({
        title: "Perfis atualizados",
        description: "As configura√ß√µes de perfil foram salvas com sucesso."
      });
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel atualizar os perfis.",
        variant: "destructive"
      });
    }
  });

  useEffect(() => {
    if (perfisData) {
      setPerfis(perfisData);
    }
  }, [perfisData]);

  const handleToggle = (id: string) => {
    setPerfis(prev => prev.map(perfil => 
      perfil.id === id 
        ? { ...perfil, ativo: !perfil.ativo }
        : perfil
    ));
  };

  const handleNomeChange = (id: string, novoNome: string) => {
    setPerfis(prev => prev.map(perfil => 
      perfil.id === id 
        ? { ...perfil, nome: novoNome }
        : perfil
    ));
  };

  const adicionarPerfil = () => {
    const novoPerfil: Perfil = {
      id: Math.random().toString(36).substring(2, 9),
      nome: 'Novo Perfil',
      ativo: true
    };
    setPerfis(prev => [...prev, novoPerfil]);
  };

  const removerPerfil = (id: string) => {
    setPerfis(prev => prev.filter(perfil => perfil.id !== id));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    mutationAtualizar.mutate(perfis);
  };

  return (
    <Card className="glass-card">
      <CardHeader>
        <CardTitle>Configura√ß√µes de Perfil</CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-4">
            {perfis.map((perfil) => (
              <div key={perfil.id} className="flex items-center gap-4 p-4 border rounded-lg">
                <div className="flex-1">
                  <Label htmlFor={`perfil-${perfil.id}`}>Nome do Perfil</Label>
                  <Input
                    id={`perfil-${perfil.id}`}
                    value={perfil.nome}
                    onChange={(e) => handleNomeChange(perfil.id, e.target.value)}
                    required
                  />
                </div>

                <div className="flex items-center gap-2">
                  <Label htmlFor={`ativo-${perfil.id}`} className="text-sm">
                    Ativo
                  </Label>
                  <Switch
                    id={`ativo-${perfil.id}`}
                    checked={perfil.ativo}
                    onCheckedChange={() => handleToggle(perfil.id)}
                  />
                </div>

                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => removerPerfil(perfil.id)}
                  className="text-red-600 hover:text-red-700"
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            ))}
          </div>

          <Button
            type="button"
            variant="outline"
            onClick={adicionarPerfil}
            className="w-full"
          >
            <Plus className="h-4 w-4 mr-2" />
            Adicionar Perfil
          </Button>

          <Button 
            type="submit" 
            disabled={mutationAtualizar.isPending}
            className="w-full"
          >
            {mutationAtualizar.isPending ? 'Salvando...' : 'Salvar Configura√ß√µes'}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
};

src/components/tasks/NewTaskDialog.tsx.content:
import React, { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { criarTarefa, buscarClientes } from '@/services/api';
import { useToast } from '@/hooks/use-toast';

interface NewTaskDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onTaskCreated: () => void;
}

export const NewTaskDialog: React.FC<NewTaskDialogProps> = ({
  open,
  onOpenChange,
  onTaskCreated
}) => {
  const [formData, setFormData] = useState({
    titulo: '',
    descricao: '',
    status: 'PENDENTE' as 'PENDENTE' | 'EM_ANDAMENTO' | 'CONCLUIDA' | 'CANCELADA',
    prioridade: 'MEDIA' as 'BAIXA' | 'MEDIA' | 'ALTA' | 'URGENTE',
    dataVencimento: '',
    clienteId: ''
  });
  const [loading, setLoading] = useState(false);
  const [clientes, setClientes] = useState<any[]>([]);
  const { toast } = useToast();

  useEffect(() => {
    if (open) {
      carregarClientes();
    }
  }, [open]);

  const carregarClientes = async () => {
    try {
      const response = await buscarClientes();
      setClientes(response.clientes || []);
    } catch (error) {
      console.error('Erro ao carregar clientes:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      await criarTarefa(formData);

      toast({
        title: "Sucesso",
        description: "Tarefa criada com sucesso",
      });

      onTaskCreated();

      // Reset form
      setFormData({
        titulo: '',
        descricao: '',
        status: 'PENDENTE',
        prioridade: 'MEDIA',
        dataVencimento: '',
        clienteId: ''
      });
    } catch (error) {
      console.error('Erro ao criar tarefa:', error);
      toast({
        title: "Erro",
        description: "Erro ao criar tarefa",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Nova Tarefa</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="titulo">T√≠tulo</Label>
            <Input
              id="titulo"
              value={formData.titulo}
              onChange={(e) => setFormData({ ...formData, titulo: e.target.value })}
              required
            />
          </div>

          <div>
            <Label htmlFor="prioridade">Prioridade</Label>
            <Select
              value={formData.prioridade}
              onValueChange={(value: 'BAIXA' | 'MEDIA' | 'ALTA' | 'URGENTE') => 
                setFormData({ ...formData, prioridade: value })
              }
            >
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="BAIXA">Baixa</SelectItem>
                <SelectItem value="MEDIA">M√©dia</SelectItem>
                <SelectItem value="ALTA">Alta</SelectItem>
                <SelectItem value="URGENTE">Urgente</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="dataVencimento">Data de Vencimento</Label>
            <Input
              id="dataVencimento"
              type="date"
              value={formData.dataVencimento}
              onChange={(e) => setFormData({ ...formData, dataVencimento: e.target.value })}
            />
          </div>

          <div>
            <Label htmlFor="cliente">Cliente (Opcional)</Label>
            <Select
              value={formData.clienteId}
              onValueChange={(value) => setFormData({ ...formData, clienteId: value })}
            >
              <SelectTrigger>
                <SelectValue placeholder="Selecione um cliente" />
              </SelectTrigger>
              <SelectContent>
                {clientes.map((cliente) => (
                  <SelectItem key={cliente.id} value={cliente.id}>
                    {cliente.nome}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="descricao">Descri√ß√£o</Label>
            <Textarea
              id="descricao"
              value={formData.descricao}
              onChange={(e) => setFormData({ ...formData, descricao: e.target.value })}
              rows={3}
            />
          </div>

          <div className="flex justify-end gap-2 pt-4">
            <Button type="button" variant="outline" onClick={() => onOpenChange(false)}>
              Cancelar
            </Button>
            <Button type="submit" disabled={loading}>
              {loading ? 'Criando...' : 'Criar Tarefa'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
};

